require('immutadot')
require('lodash')
const { mapValues, orderBy } = require('immutadot-lodash')

mapValues({ nested: { map: { a: 1, b: 2, c: 3 } } }, 'nested.map', v => v * v)
// => { nested: { map: { a: 1, b: 4, c: 9 } } }

orderBy({ nested: { people: [{ name: 'Nico', age: 30 }, { name: 'Yvo', age: 2 }, { name: 'Nico', age: 666 }] } }, 'nested.people', ['name', 'age'], ['desc', 'desc'])
// => { nested: { people: [{ name: 'Yvo', age: 2 }, { name: 'Nico', age: 666 }, { name: 'Nico', age: 30 }] } }
